/*!
  =========================================================
  * Muse Ant Design Dashboard - v1.0.0
  =========================================================
  * Product Page: https://www.creative-tim.com/product/muse-ant-design-dashboard
  * Copyright 2021 Creative Tim (https://www.creative-tim.com)
  * Licensed under MIT (https://github.com/creativetimofficial/muse-ant-design-dashboard/blob/main/LICENSE.md)
  * Coded by Creative Tim
  =========================================================
  * The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.
*/

import ReactApexChart from "react-apexcharts";
import { Row, Col, Typography } from "antd";
import eChart from "./configs/eChart";
import { useEffect, useState } from "react";
import axios from "axios";
import url from "../host";
function EChart() {
  const { Title, Paragraph } = Typography;
  // const [data, setData] = useState('');
  const [data2, setData2] = useState('');
  const [data3, setData3] = useState('');
  // const [data4, setData4] = useState('');
  useEffect(() => {
    axios
    .get(`${url}/api/car_history/`)
    .then((res) => {
      const today = new Date().toISOString().slice(5, 7); 
      const filteredData = res.data.filter((item) => {
        return item.time_create.slice(5,7) === today; 
      });
      const totalPrice = filteredData.reduce((acc, cur) => {
        return acc + cur.price;
      }, 0);
      // const totalinitialprice = filteredData.reduce((acc, cur) => {
      //   return acc + cur.initial_price;
      // }, 0);
      // const profit=totalPrice-totalinitialprice
      // setData(profit);
      setData2(totalPrice)
      console.log(totalPrice, "totalprice");
      console.log(today,"time");
      // console.log(totalinitialprice, "totalinitialprice");
      // console.log(profit,"profit");
      axios
      .get(`${url}/auth/users/`,{
        headers: { Authorization: "Bearer " + sessionStorage.getItem("token") },
      })
      .then((res2) => {
      console.log(res2.data,"user");
      const today2 = new Date().toISOString().slice(5,7); 
      const totalUsers = res2.data.length
      const todayUsers=res2.data.filter(item => 
        item.date_joined.slice(5,7) === today2
      )
      console.log(totalUsers);
      console.log(todayUsers,"today");
      setData3(totalUsers)
      // setData4(todayUsers.length)
      })
    })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  const items = [
    {
      Title: data3,
      user: "Users",
    },
    {
      Title: "2m",
      user: "Clicks",
    },
    {
      Title: `${data2}$`,
      user: "Sales",
    },
    {
      Title: "82",
      user: "Items",
    },
  ];

  return (
    <>
      <div id="chart">
        <ReactApexChart
          className="bar-chart"
          options={eChart.options}
          series={eChart.series}
          type="bar"
          height={220}
        />
      </div>
      <div className="chart-vistior">
        <Title level={5}>Active Users</Title>
        <Paragraph className="lastweek">
          than last week <span className="bnb2">+30%</span>
        </Paragraph>
        <Paragraph className="lastweek">
          We have created multiple options for you to put together and customise
          into pixel perfect pages.
        </Paragraph>
        <Row gutter>
          {items.map((v, index) => (
            <Col xs={6} xl={6} sm={6} md={6} key={index}>
              <div className="chart-visitor-count">
                <Title level={4}>{v.Title}</Title>
                <span>{v.user}</span>
              </div>
            </Col>
          ))}
        </Row>
      </div>
    </>
  );
}

export default EChart;
